mapper.select:
WITH RECURSIVE rel (child_no, no, parent_no) AS (
SELECT d.NO AS child_no, d.NO AS no, d.PARENT_NO FROM BN_DIRECTORY d WHERE d.PARENT_NO = #{parentNo}
UNION ALL
SELECT rel.child_no, p.NO, p.PARENT_NO FROM BN_DIRECTORY p JOIN rel ON rel.parent_no = p.NO ), readable (child_no) AS ( SELECT DISTINCT rel.child_no FROM rel JOIN BN_DIRECTORY_ACCESS da ON da.DIR_NO = rel.no JOIN BN_USER ugm ON ugm.GROUP_NO = da.GROUP_NO WHERE ugm.NO = #{userNo} AND BITAND(da.ACCESS_FLAGS, 1) = 1 ) SELECT d.NO AS no, CAST(d.UID AS VARCHAR) AS uid, d.NAME AS name, d.PARENT_NO AS parentNo, EXISTS (SELECT 1 FROM BN_DIRECTORY c WHERE c.PARENT_NO = d.NO) AS hasChild FROM BN_DIRECTORY d WHERE d.PARENT_NO = #{parentNo} AND EXISTS (SELECT 1 FROM readable r WHERE r.child_no = d.NO) ORDER BY d.NAME;
