mapper.insert:
    INSERT INTO BN_DIRECTORY (
    	UPDATE_DATE
    	, ENABLED
    	, PUBLIC_ACCESSIBLE
    	, PARENT_NO
    	, OWNER_NO
    	, HAS_CHILD
    	, CREATION
		, LAST_MODIFIED 
		, NAME
		, ABSOLUTE_PATH	
	) VALUES (
		CURRENT_TIMESTAMP
		, #{enabled}
		, #{publicAccessible}
		, #{parentNo}
		, #{ownerNo}
		, #{hasChild}
		, #{creation}
		, #{lastModified}
		, #{name}
		, #{absolutePath}
	)

mapper.update:
    UPDATE BN_DIRECTORY
    SET UPDATE_DATE = CURRENT_TIMESTAMP
    	, ENABLED = #{enabled}
    	, PUBLIC_ACCESSIBLE = #{publicAccessible}
    	, PARENT_NO = #{parentNo}
    	, HAS_CHILD = #{hasChild}
		, LAST_MODIFIED = #{lastModified}
		, NAME = #{name}
		, ABSOLUTE_PATH	= #{absolutePath}
    WHERE NO = #{no}

